import React, { useState } from "react";
import uuid from "uuid";
import axios from "axios";

const useAxios = url => {
  const [data, setData] = useState([]);
  const addCard = async () => {
    const response = await axios.get(url);
  };
  setData(data => [...data, { ...response.data, id: uuid() }]);
  return [data, addCard];
};

export default useAxios;
